import {action, makeObservable, observable} from 'mobx';

class Store {
  allTask = [];
  constructor() {
    makeObservable(this, {
      allTask: observable,
      input: action.bound
    })
    }
    input = (inp) => {
      let allTask = this.allTask
      console.log(this.allTask)
      return allTask.push(inp)
    }
    delete = (del) => {
      let allTask = this.allTask
      allTask.map( v => {
        if(del==v)
          delete allTask.v
      })
    }
}

export default new Store()
