import React, {useState} from 'react';
import {observer} from 'mobx-react';
import store from './store';
import styled from 'styled-components';

function App() {;
  const Button = styled.button({
    color: '#8764b5',
    backgroundColor: 'white',
    fontSize: '20px',
    margin: '5px',
    padding: '5px 15px',
    border: '1px solid #8764b5',
    borderRadius: '5px'
  })
  const Input = styled.input({color: '#8764b5', fontSize: '20px', padding: '5px 10px', border: '1px solid #8764b5', borderRadius: '5px'})
  const Li = styled.li({listStyleType: 'none', color: '#8764b5', fontSize: '30px'})
  const Ul = styled.ul({margin: '0', padding: '40px 0'})
  const MainContainer = styled.div({margin: '0 auto', width:"400px", border:'1px solid #8764b5', borderRadius: '5px'})

  const [Task,
    setTask] = useState('')

  const Click = () => {
    if (Task != null) 
      store.input(Task);
    return setTask('')
  }

  return (
    <div className="App">
      <MainContainer>
        <Ul>
          {store
            .allTask
            .map(t => {
              return <Li>{t}</Li>
            })
}
        </Ul>
        <Input value={Task} onChange={e => setTask(e.target.value)}/>
        <Button onClick={Click}>ADD</Button>
      </MainContainer>
    </div>
  );
}

export default observer(App);
