import { action, makeObservable, observable} from 'mobx';

class Store{
    value = 1;
    constructor(value){
        makeObservable(this, {
            value: observable,
            sum: action
        })
        value = this.value.bind(this)
    }
    sum(){
        return this.value++
    }
}


export default new Store()
