import {action, makeObservable, observable} from 'mobx';

class Store {
  allTask = [];
  constructor() {
    makeObservable(this, {
      allTask: observable,
      input: action.bound
    })
  }
  input = (inp) => {
    let allTask = this.allTask
    console.log(allTask)
    return allTask.push({id: allTask.length, title: inp})
  }
