import {action, makeObservable, observable} from 'mobx';

class Store {
  value = 1;
  constructor() {
    makeObservable(this, {
      value: observable,
      sum: action
    })
  }
  setValue(payload){
    this.value = payload
  }
  sum = () => {
      console.log(this.value)
      this.setValue(this.value)
    return this.value++
  }
}

export default new Store()
